import { useState } from 'react';

const useFilterPlanets = (input, context) => {
  const [filteredṔlanets, setFilterPlanets] = useState();

  if (!context.results && !input) {
    const filtered = context.results.filter((result) => result.name.includes(input));
    setFilterPlanets(filtered);
    return [filteredṔlanets, setFilterPlanets];
  }
};

export default useFilterPlanets;
