import React, { useContext, useState } from 'react';
import PlanetsContext from '../context/PlanetsContext';
// import useFilterPlanets from '../hooks/filterPlanets';

function Header() {
  // const [input, setInputValue] = useState('');
  // useFilterPlanets(input, context);
  const { filterPlanets } = useContext(PlanetsContext);
  return (
    <header>
      <form>
        <input
          className="input is-success"
          type="text"
          data-testid="name-filter"
          placeholder="Digite Um Planeta de Star Wars"
          onChange={ ({ target: { value } }) => filterPlanets(value) }
        />
      </form>
    </header>
  );
}

export default Header;
