import { useState } from 'react';

const useFilterPlanets = (input, { results }) => {
  const [filteredṔlanets, setFilterPlanets] = useState;

  if (results !== undefined) {
    const filtered = results.filter((result) => result.name.includes(input));
    return [filteredṔlanets, setFilterPlanets];
  }
};

export default useFilterPlanets;
