import { useState } from 'react';
import useMountPlanets from './useMountPlanets';

const useFilterPlanets = (input, context) => {
  const [planets, setPlanets] = useMountPlanets();

  if (context.results !== undefined && input !== '') {
    const filtered = context.results.filter((result) => result.name.includes(input));
    set(filtered);
  }
};

export default useFilterPlanets;
