import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import PlanetsContext from './PlanetsContext';
import useMountPlanets from '../hooks/useMountPlanets';
import filterPlanets from '../hooks/filterPlanets';

function PlanetsProvider({ children }) {
  const [planets] = useMountPlanets();
  const [planetInput, setPlanetInput] = useState('');
  const [filteredPlanets, setFilteredPlanets] = useState([]);

  useEffect(() => {
    setFilteredPlanets(planets);
  }, [planets]);

  useEffect(() => {
    if (planetInput !== '') {
      const filterResult = filterPlanets(planetInput, planets);
      setFilteredPlanets(filterResult);
    }
  }, [filterResult]);

  const planetsContext = {
    planets: filteredPlanets,
    filterPlanets: (input) => {
      setPlanetInput(input);
    },
  };

  console.log(planetInput);

  return (
    <PlanetsContext.Provider value={ planetsContext }>
      {children}
    </PlanetsContext.Provider>
  );
}

PlanetsProvider.propTypes = {
  children: PropTypes.objectOf(Object).isRequired,
};

export default PlanetsProvider;
